import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
// import MoreIcon from '@material-ui/icons/More';
import VisibilityIcon from '@material-ui/icons/Visibility';
import styles from './Order.module.scss';


import { connect } from 'react-redux';
import { getCakes, getCupcakes } from '../../../redux/productsRedux';

const useStyles = makeStyles((theme) => ({
  // gridList: {
  //   flexWrap: 'nowrap',
  // },
  title: {
    color: '#EF98AE',
  },
  swiperDiv: {
    marginBottom: '40px',
  },
}));

function Component ({cakes, cupcakes}) {
  const classes = useStyles();

  return (
    <div className={styles.root}>
      {/* Cakes horizontal swiper */}
      <div className={classes.swiperDiv}>
        <h2 className={styles.title}>These are our cakes: </h2>
        <GridList spacing={15} className={classes.gridList} cols={Math.max(document.documentElement.clientWidth) > 600 ? 2.2 : 1.2} cellHeight={230}>
          {cakes.map((tile) => (
            <GridListTile key={tile._id}>
              <img className={styles.image} src={require('../../../images/products/cakes/' + tile.img)} alt={tile.title} />
              <GridListTileBar
                title={tile.title}
                actionIcon={
                  <IconButton >
                    <VisibilityIcon className={classes.title} />
                  </IconButton>
                }
              />
            </GridListTile>
          ))}
        </GridList>
      </div>
      {/* Cupcakes horizontal swiper */}
      <div className={classes.swiperDiv}>
        <h2 className={styles.title}>... and cupcakes: </h2>
        <GridList className={classes.gridList} cols={Math.max(document.documentElement.clientWidth) > 600 ? 2.2 : 1.2} cellHeight={230}>
          {cupcakes.map((tile) => (
            <GridListTile key={tile._id}>
              <img className={styles.image} src={require('../../../images/products/cupcakes/' + tile.img)} alt={tile.title} />
              <GridListTileBar
                title={tile.title}
                actionIcon={
                  <IconButton >
                    <VisibilityIcon className={classes.title} />
                  </IconButton>
                }
              />
            </GridListTile>
          ))}
        </GridList>
      </div>
    </div>
  );
}

Component.propTypes = {
  cakes: PropTypes.array,
  cupcakes: PropTypes.array,
};

const mapStateToProps = state => ({
  cakes: getCakes(state),
  cupcakes: getCupcakes(state),
});

// const mapDispatchToProps = dispatch => ({
//   someAction: arg => dispatch(reduxActionCreator(arg)),
// });

const Container = connect(mapStateToProps)(Component);

export {
  // Component as Order,
  Container as Order,
  Component as OrderComponent,
};
