import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';

// import clsx from 'clsx';

import { connect } from 'react-redux';
import { getCakes, getCupcakes } from '../../../redux/productsRedux';

// import styles from './Order.module.scss';
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
  },
  gridList: {
    flexWrap: 'nowrap',
  },
}));

function Component ({cakes, cupcakes}) {
  const classes = useStyles();
  // <div className={clsx(styles.root)}>
  //   <h3>Cakes</h3>
  //   {cakes.map(cake => (
  //     <p key={cake._id}>{cake.title + ' $' + cake.price}</p>
  //   ))}
  //   <h3>Cupcakes</h3>
  //   {cupcakes.map(cupcake => (
  //     <p key={cupcake._id}>{cupcake.title + ' $' + cupcake.price}</p>
  //   ))}
  // </div>
  return (
    <div className={classes.root}>
      <GridList className={classes.gridList} cols={2.5}>
        {cakes.map((tile) => (
          <GridListTile key={tile._id}>
            <img src={tile.img} alt={tile.title} />
            <GridListTileBar
              title={tile.title}
            />
          </GridListTile>
        ))}
        {cakes.map(cake => (
          <p key={cake._id}>{cake.title}</p>
        ))}
      </GridList>
    </div>
  )
};

Component.propTypes = {
  cakes: PropTypes.array,
  cupcakes: PropTypes.array,
};

const mapStateToProps = state => ({
  cakes: getCakes(state),
  cupcakes: getCupcakes(state),
});

// const mapDispatchToProps = dispatch => ({
//   someAction: arg => dispatch(reduxActionCreator(arg)),
// });

const Container = connect(mapStateToProps)(Component);

export {
  // Component as Order,
  Container as Order,
  Component as OrderComponent,
};
