import React from 'react';
import PropTypes from 'prop-types';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

import clsx from 'clsx';

import { connect } from 'react-redux';
import { getCart, addToCart, removeCart } from '../../../redux/cartRedux';

import styles from './Cart.module.scss';
import { Typography } from '@material-ui/core';

class Component extends React.Component {

  render(){
    const { cart } = this.props;
    console.log(cart);
    return(
      <div className={clsx(styles.root)}>
        <h2>Cart:</h2>
        <div className={styles.container}>
          <List>
            {cart.map(product => (
              <ListItem key={product._id} button>
                {console.log(product.title)}
                <ListItemAvatar>
                  <Avatar
                    srcSet={product.type === 'cake' ? require('../../../images/products/cakes/' + product.img) : require('../../../images/products/cupcakes/' + product.img)}
                    variant='square'
                    className={styles.avatar}
                  />
                </ListItemAvatar>
                <ListItemText
                  primary={product.title}
                  secondary={
                    <React.Fragment>
                      <Typography
                        variant='subtitle2'
                      >
                        {'price: $' + product.price}
                      </Typography>
                      <Typography
                        variant='subtitle2'
                      >
                        {'amount: x' + product.amount}
                      </Typography>
                      <Typography
                        variant='subtitle2'
                      >
                        {'total cost: $' + product.totalCost}
                      </Typography>
                      {product.wishes ? (
                        <Typography
                          variant='subtitle2'
                        >
                          {'total cost: $' + product.wishes}
                        </Typography>
                      ) : ''}
                    </React.Fragment>
                  }
                />
                <ListItemSecondaryAction>
                  <IconButton edge="end" aria-label="delete">
                    <DeleteIcon />
                  </IconButton>
                </ListItemSecondaryAction>
              </ListItem>
            ))}
          </List>
        </div>
      </div>
    );
  }
}

Component.propTypes = {
  cart: PropTypes.array,
};

const mapStateToProps = state => ({
  cart: getCart(state),
});

const mapDispatchToProps = dispatch => ({
  addToCart: data => dispatch(addToCart(data)),
  removeCart: () => dispatch(removeCart()),
});

const Container = connect(mapStateToProps, mapDispatchToProps)(Component);

export {
  // Component as Cart,
  Container as Cart,
  Component as CartComponent,
};
