import React from 'react';
import PropTypes from 'prop-types';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

import clsx from 'clsx';

import { connect } from 'react-redux';
import { getCart, addToCart, removeCart } from '../../../redux/cartRedux';

import styles from './Cart.module.scss';

class Component extends React.Component {

  render(){
    const { cart } = this.props;
    console.log(cart);
    return(
      <div className={clsx(styles.root)}>
        <div className={styles.container}>
          <List>
            {cart.map(product => (
              <ListItem key={product._id} selected={selectedIndex === 0}>
                {console.log(product.title)}
                <ListItemAvatar>
                  <Avatar
                    src={product.type === 'cake' ? require('../../../images/products/cakes/' + product.img) : require('../../../images/products/cupcakes/' + product.img)}
                    variant='square'
                  />
                </ListItemAvatar>
                <ListItemText
                  primary={product.title}
                  secondary={'Price: ' + product.price + '    Amount: ' + product.amount}
                />
                <ListItemSecondaryAction>
                  <IconButton edge="end" aria-label="delete">
                    <DeleteIcon />
                  </IconButton>
                </ListItemSecondaryAction>
              </ListItem>
            ))}
          </List>
        </div>
      </div>
    );
  }
}

Component.propTypes = {
  cart: PropTypes.array,
};

const mapStateToProps = state => ({
  cart: getCart(state),
});

const mapDispatchToProps = dispatch => ({
  addToCart: data => dispatch(addToCart(data)),
  removeCart: () => dispatch(removeCart()),
});

const Container = connect(mapStateToProps, mapDispatchToProps)(Component);

export {
  // Component as Cart,
  Container as Cart,
  Component as CartComponent,
};
