import React from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import MoreIcon from '@material-ui/icons/More';

// import clsx from 'clsx';

import { connect } from 'react-redux';
import { getCakes, getCupcakes } from '../../../redux/productsRedux';

// import styles from './Order.module.scss';
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    width: '80%',
    margin: '30px auto',
  },
  gridList: {
    flexWrap: 'nowrap',
  },
  title: {
    color: 'white',
  },
}));

function Component ({cakes, cupcakes}) {
  const classes = useStyles();
  const vw = Math.max(document.documentElement.clientWidth);
  console.log('vw ', vw);
  return (
    <div className={classes.root}>
      <GridList className={classes.gridList} cols={2.1} cellHeight={230}>
        {cakes.map((tile) => (
          <GridListTile key={tile._id}>
            <img src={require('../../../images/products/cakes/' + tile.img)} alt={tile.title} />
            <GridListTileBar
              title={tile.title}
              actionIcon={
                <IconButton >
                  <MoreIcon className={classes.title} />
                </IconButton>
              }
            />
          </GridListTile>
        ))}
      </GridList>
    </div>
  );
}

Component.propTypes = {
  cakes: PropTypes.array,
  cupcakes: PropTypes.array,
};

const mapStateToProps = state => ({
  cakes: getCakes(state),
  cupcakes: getCupcakes(state),
});

// const mapDispatchToProps = dispatch => ({
//   someAction: arg => dispatch(reduxActionCreator(arg)),
// });

const Container = connect(mapStateToProps)(Component);

export {
  // Component as Order,
  Container as Order,
  Component as OrderComponent,
};
