import React from 'react';
import PropTypes from 'prop-types';

import clsx from 'clsx';

import { connect } from 'react-redux';
import { getCakes, getCupcakes } from '../../../redux/productsRedux';

import styles from './Product.module.scss';

class Component extends React.Component {
  getOne = ({id, type}) => {
    console.log(this.props.cakes);

    if(type === 'cake'){
      return this.props.cakes.find(product => product._id === id);
    } else {
      return this.props.cupcakes.find(product => product._id === id);
    }
  };

  render(){
    const type = this.props.match.params.id.split('-')[0];
    const id = this.props.match.params.id.split('-')[1];
    const product = this.getOne(id, type);
    console.log(product);

    return (
      <div className={clsx(styles.root)}>
        <h2>Product</h2>
      </div>
    );
  }
}

Component.propTypes = {
  cakes: PropTypes.array,
  cupcakes: PropTypes.array,
  match: PropTypes.object,
};




const mapStateToProps = state => ({
  cakes: getCakes(state),
  cupcakes: getCupcakes(state),
});

// const mapDispatchToProps = dispatch => ({
//   someAction: arg => dispatch(reduxActionCreator(arg)),
// });

const Container = connect(mapStateToProps)(Component);

export {
  // Component as Product,
  Container as Product,
  Component as ProductComponent,
};
